import java.util.Scanner; public class RaiseToApower {public static void main (String[] args) {Scanner in1 = new Scanner(System.in);System.out.println("Enter a number");double number = in1.nextDouble();Scanner in2 = new Scanner(System.in);System.out.println("Enter the power to be raised to");int indice = in2.nextInt();System.out.println(cube(number, indice));in1.close();in2.close();}public static double cube(double val, int power) {if (power == 0) {return val;} else if (power > 0) {double runMultiply = val;for (int i = 0; i < power - 1; i++) {runMultiply *= val;}return runMultiply;} else if (power < 0) {int powerNew = Math.abs(power);double runMutiply = val;for (int i = 0; i < powerNew - 1; i++) {runMutiply *= val;}return 1/runMutiply;}return 0;}}